INSERT INTO `book` VALUES ('20', '我居然认得上古神文', '三个皮蛋', '2023-12-01 03:43:45', '穿越后的钟文被一个小萝莉捡回了只有女弟子的飘花宫； 这个世界最顶尖的功法、灵技、知识统统来自上古； 于是，研究看不懂的上古文字，成了时代的潮流、身份地位的象征；', '完结', '玄幻', '1500', '1000', '1997-06-26 00:00:00', 'cover20.jpg', '733');
